"use client"

import { useEffect } from "react"
import { WebSocketService } from "@/lib/websocket"
import { showBankNotificationAlert } from "./notification-alert"
import type { Notification } from "@/types/notification"

export const AdminNotificationHandler = () => {
  useEffect(() => {
    console.log('Admin notification handler initialized')
    const wsService = new WebSocketService()

    // Set up WebSocket event handlers for admin pages only
    wsService.onNotification((notification: Notification) => {
      console.log('Admin: New notification received', notification)
      // Show bank-style alert for new notification in admin
      showBankNotificationAlert(notification)
    })

    // Handle connection status
    wsService.onConnect(() => {
      console.log('Admin: Connected to WebSocket for notifications')
    })

    wsService.onDisconnect(() => {
      console.log('Admin: Disconnected from WebSocket')
    })

    // Connect to WebSocket
    wsService.connect()

    return () => {
      console.log('Admin notification handler cleanup')
      wsService.disconnect()
    }
  }, [])

  // This component doesn't render anything visible
  return null
}

"use client"

import { useEffect } from "react"
import { WebSocketService } from "@/lib/websocket"
import { showBankNotificationAlert, showBankNotificationSummary } from "./notification-alert"
import type { Notification } from "@/types/notification"

export const AdminNotificationHandler = () => {
  useEffect(() => {
    console.log('Admin notification handler initialized')

    // Create a single WebSocket connection for admin
    const wsService = new WebSocketService()

    // Set up WebSocket event handlers for admin pages
    wsService.onNotification((notification: Notification) => {
      console.log('Admin: New notification received', notification)

      // Show bank-style alert for new notification in admin
      showBankNotificationAlert(notification)

      // Also show the "new notifications" summary
      showBankNotificationSummary(1, 'newNotifications')
    })

    // Handle connection status
    wsService.onConnect(() => {
      console.log('Admin: Connected to WebSocket for notifications')
    })

    wsService.onDisconnect(() => {
      console.log('Admin: Disconnected from WebSocket')
    })

    // Connect to WebSocket
    wsService.connect()

    return () => {
      console.log('Admin notification handler cleanup')
      wsService.disconnect()
    }
  }, [])

  // This component doesn't render anything visible
  return null
}

"use client"

import React from "react"
import { Bell, CheckCircle, AlertTriangle, Info, XCircle, CreditCard, ArrowUpRight, ArrowDownLeft } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import type { Notification } from "@/types/notification"

// Bank-style notification icons based on type and content
const getNotificationIcon = (notification: Notification) => {
  const { type, title, message } = notification

  // Check for banking-specific keywords
  const content = `${title} ${message}`.toLowerCase()

  if (content.includes('transfer') || content.includes('sent')) {
    return <ArrowUpRight className="h-5 w-5 text-red-500" />
  }
  if (content.includes('received') || content.includes('deposit')) {
    return <ArrowDownLeft className="h-5 w-5 text-green-500" />
  }
  if (content.includes('payment') || content.includes('card')) {
    return <CreditCard className="h-5 w-5 text-blue-500" />
  }

  // Fallback to type-based icons
  switch (type) {
    case "success":
      return <CheckCircle className="h-5 w-5 text-green-500" />
    case "warning":
      return <AlertTriangle className="h-5 w-5 text-yellow-500" />
    case "error":
      return <XCircle className="h-5 w-5 text-red-500" />
    default:
      return <Info className="h-5 w-5 text-blue-500" />
  }
}

// Get notification styling based on type
const getNotificationStyle = (notification: Notification) => {
  const { type, title, message } = notification
  const content = `${title} ${message}`.toLowerCase()

  if (content.includes('transfer') || content.includes('sent')) {
    return {
      variant: "default" as const,
      className: "border-l-4 border-l-red-500 bg-red-50 dark:bg-red-950/20"
    }
  }
  if (content.includes('received') || content.includes('deposit')) {
    return {
      variant: "default" as const,
      className: "border-l-4 border-l-green-500 bg-green-50 dark:bg-green-950/20"
    }
  }
  if (content.includes('payment') || content.includes('card')) {
    return {
      variant: "default" as const,
      className: "border-l-4 border-l-blue-500 bg-blue-50 dark:bg-blue-950/20"
    }
  }

  switch (type) {
    case "success":
      return {
        variant: "default" as const,
        className: "border-l-4 border-l-green-500 bg-green-50 dark:bg-green-950/20"
      }
    case "warning":
      return {
        variant: "default" as const,
        className: "border-l-4 border-l-yellow-500 bg-yellow-50 dark:bg-yellow-950/20"
      }
    case "error":
      return {
        variant: "destructive" as const,
        className: "border-l-4 border-l-red-500"
      }
    default:
      return {
        variant: "default" as const,
        className: "border-l-4 border-l-blue-500 bg-blue-50 dark:bg-blue-950/20"
      }
  }
}

// Format amount for banking notifications
const formatAmount = (text: string) => {
  const amountMatch = text.match(/\$?([0-9,]+\.?[0-9]*)/g)
  if (amountMatch) {
    return text.replace(/\$?([0-9,]+\.?[0-9]*)/g, (match) => {
      const cleanAmount = match.replace(/[$,]/g, '')
      return `$${parseFloat(cleanAmount).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`
    })
  }
  return text
}

export const showBankNotificationAlert = (notification: Notification) => {
  const { toast } = useToast()
  const icon = getNotificationIcon(notification)
  const style = getNotificationStyle(notification)

  toast({
    ...style,
    duration: 8000, // 8 seconds for bank notifications
    title: (
      <div className="flex items-center gap-2">
        {icon}
        <span className="font-semibold">{notification.title}</span>
      </div>
    ),
    description: (
      <div className="mt-1">
        <p className="text-sm">{formatAmount(notification.message)}</p>
        <p className="text-xs text-muted-foreground mt-1">
          {new Date(notification.timestamp).toLocaleTimeString('en-US', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: true
          })}
        </p>
      </div>
    ),
  })
}

// Show multiple notifications as a summary (for bulk updates)
export const showBankNotificationSummary = (count: number, type: 'markRead' | 'newNotifications') => {
  const { toast } = useToast()
  const icon = type === 'markRead'
    ? <CheckCircle className="h-5 w-5 text-green-500" />
    : <Bell className="h-5 w-5 text-blue-500" />

  const title = type === 'markRead'
    ? "Notifications Updated"
    : "New Notifications"

  const message = type === 'markRead'
    ? `${count} notification${count > 1 ? 's' : ''} marked as read`
    : `${count} new notification${count > 1 ? 's' : ''} received`

  toast({
    variant: "default",
    className: "border-l-4 border-l-blue-500 bg-blue-50 dark:bg-blue-950/20",
    duration: 4000,
    title: (
      <div className="flex items-center gap-2">
        {icon}
        <span className="font-semibold">{title}</span>
      </div>
    ),
    description: <p className="text-sm">{message}</p>,
  })
}

